oxy_webhelp_navlinks = {"cover_read-d741e569":{"topics":[{"title":"使用Zotero阅读文献","shortdesc":"<p class=\"shortdesc\">Zotero作为文献管理软件，阅读文献是最基础的一项功能。为了使阅读、管理文献的过程更加便捷、流畅，此文档提供使用Zotero打开、阅读文献的方式以及一些功能的介绍。<\/p>","href":"6文献阅读与引用\/How_to_read.html","attributes":{"data-id":"task_yzk_gsw_kdc"},"menu":{"hasChildren":true},"tocID":"task_yzk_gsw_kdc-d741e580","next":"task_yzk_gsw_kdc-d741e580"},{"title":"如何使用Zotero引用文献（Microsoft Word）","shortdesc":"<p class=\"shortdesc\">通过引用功能能够使文献管理软件在阅读和写作过程当中实现很好的交互，以提高文献管理效率。<\/p>","href":"6文献阅读与引用\/how%20to%20cite.html","attributes":{"data-id":"task_py4_rt5_ldc"},"menu":{"hasChildren":true},"tocID":"task_py4_rt5_ldc-d741e632","next":"task_py4_rt5_ldc-d741e632"}]}};